<div class="box box-widget widget-user-2">
    <div class="widget-user-header bg-<%= panelBgColor %>">
        <div class="widget-user-image">
            <i class="fa <%= panelFAIconClass %>"></i>
        </div>
        <h3 class="widget-user-username"><%= panelName %></h3>
        <h5 class="widget-user-desc"><%= panelDescription %>&nbsp;</h5>
    </div>
    <div class="box-footer no-padding panel-links-list-container">
        <%= panelLinksListView %>
    </div>
</div>
