<div data-tab-name="<%= TabName %>" class="<%= TabName %> tabButtonPane" style="display:none">
    <div class="pull-right tab-pagination-controls pagination-controls"></div>
    <button type="button" class="btn-add-model btnTabAdd btn btn-xs btn-primary">Add New <%= btnLabel %></button>
    <button type="button" class="btn-edit-model hide btn btn-xs btn-primary">Edit <%= btnLabel %></button>
    <button type="button" class="btn-delete-checked-models btnTabDeleteChecked disabled btn btn-xs btn-danger">Delete <%= btnLabel %> Chosen</button>
    <button type="button" class="btn-reset-columns btnTabClearStored btn btn-xs btn-default">Reset <%= btnLabel %> Columns</button>
    <div class="columnmanager-visibilitycontrol-container"></div>
</div>
